def FindWold(path):
    os.scandir(path)
def ReadTxtFile(file):
    if os.path.exists(file):
        cun=0
        FF = open(file, 'r', encoding='utf-8')
        for f in  FF:
            if cun == 100: return
            # print(f.readline(), end='')
            lb.insert(tk.END, f)
            cun+=1
        FF.close()
ReadTxtFile('2023-07-24_2023-07-28.TXT')



